import styled from "styled-components";

export const StyledDivInput = styled.div`
    background-color: red;
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 15px;

    label{
        font-weight: 500;
        font-size: 14px;
        line-height: 17px;
        color: #000000;
    }
    input{
        height: 34px;
    }


`
