import styled from "styled-components";

export const StyleMain = styled.main`
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #A25858;

    position: relative;

    padding: 30px;

    @media (max-width: 315px){
        padding: 30px 10px;
        align-items: baseline;
    } 
    div{
        width: 100%;
        height: 85%;
        display: flex;
        align-items: center;
        justify-content: center;

        img{
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 20px;
        }
    }

     fieldset{
        width: 80%;

        position: absolute;
        background: rgba(232, 77, 77, 0.65);
        border-radius: 20px;
        transform: rotate(-0.12deg);
        text-align: center;

        padding: 30px;
        @media screen {

        }()

        p{
            font-weight: 400;
            font-size: 18px;
            line-height: 150%;
            color: #FFFFFF;

        }
     }

`
