import styled from "styled-components";

export const StyleMain = styled.main`
    width: 100%;
    min-height: 92.5vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #DCDBDB;
    padding: 1.875rem;

    @media (max-width: 19.6875rem){
        padding: 1.875rem 0.625rem;
        align-items: baseline;
    } 
    div{
        width: 100%;
        max-width: 1200px;
        height: 700px;

        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;   

        @media(max-width: 375px){
            height: 550px;
        }
        img{
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 1.25rem;
        }
    }



`
